import React,{useState} from "react";
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';

import './styles.scss';

function ListStarships(props) {
  const [timeCool, setTimeCool] = useState(0);
  const stops = () => {
    props.allShips.map((item) => {
      const consumables = item.consumables.split(' ');
        switch (consumables[1]) {
          case 'day':
            case 'days':
              setTimeCool(parseInt(consumables[0]) * 24);
            break;
            case 'week':
          case 'weeks':
            setTimeCool(parseInt(consumables[0]) * 168);
            break;
            case 'month':
            case 'months':
              setTimeCool(parseInt(consumables[0]) * 730);
            break;
            case 'year':
          case 'years':
              setTimeCool(parseInt(consumables[0]) * 8760);
              break;
          default:
            setTimeCool(0);
            break;
        }
    })
  }

  return (
    <>
       <section>
        <List component="nav" aria-label="main mailbox folders">
          {props.allShips.map(item => item && item.name ? 
            <ListItem className="ships">
              <ListItemText className="shipsName">{item.name} - Will make {1000000 / (item.MGLT * stops(item.consumables))} {console.log(stops(item.consumables))}stops</ListItemText>
            </ListItem> : <p></p>)
          }
        </List>
      </section>
    </>
  );
};

export default ListStarships;
