import React, { useState, useEffect } from "react";
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';

import './styles.scss';

function ListStarships(props) {
  const [timeCool, setTimeCool] = useState([])

  const stops = () => {
    const consumables = props.allShips.map(item => item && item.consumables ? 
      item.consumables.split(' ') : undefined);

      switch (consumables[1]) {
				case 'day':
					case 'days':
          setTimeCool(parseInt(consumables[0]) * 24);
					break;
					case 'week':
				case 'weeks':
					setTimeCool(parseInt(consumables[0]) * 168);
					break;
					case 'month':
					case 'months':
					setTimeCool(parseInt(consumables[0]) * 730);
					break;
					case 'year':
				case 'years':
						setTimeCool(parseInt(consumables[0]) * 8760);
						break;
				default:
						setTimeCool(0);
					break;
        }
  }

  useEffect(() => {
    stops();
    // eslint-disable-next-line
  },[])
  console.log(timeCool)
  return (
    <>
       <section>
        <List component="nav" aria-label="main mailbox folders">
          {props.allShips.map(item => item && item.name ? 
            <ListItem className="ships">
              <ListItemText className="shipsName">{item.name} - Will make {timeCool} stops</ListItemText>
            </ListItem> : <p></p>)
          }
        </List>
      </section>
    </>
  );
};

export default ListStarships;
