import React, {useState} from "react";
import {TextField, Button} from '@material-ui/core';

import './styles.scss'

function Input () {

  const [term, setTerm] = useState("");
  const handleChange = e => {
    setTerm(e.target.value);
  };

  const handleSubmit = e => {
    e.preventDefault();
    onTerm(term);
  };

  return (
    <>
      <form onSubmit={handleSubmit}>
        <TextField  className="textField" label="Distance in MGLT?" type="number" onChange={handleChange} variant="filled" />
        <Button  className="button" variant="contained" color="primary">stops</Button>
      </form>
    </>
  );
};

export default Input;
