import React, {useState} from "react";
import Header from "../../components/Header";
import DistanceEntry from "../../components/DistanceEntry";
import ListStarship from "../../components/ListStarship";
import ListShips from "../../services";

function Home() {

    const [shipsAll, setShipsAll] = useState([]);
    const [distance, setDistance] = useState(0);
    const [stopsShip, setStopsShip] = useState(0);

    async function handleSubmit(distance) {
        const response = await ListShips.get({
        });
        setShipsAll(response.data.results)
      }

    return (
        <>
            <Header />
            <DistanceEntry distanceMglt={handleSubmit}/>
            <ListStarship allShips={shipsAll} stops={distance / (shipsAll.mglt * shipsAll.consumables)} />
        </>
    )
}
export default Home;
