import React, { useState } from "react";
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';

import './styles.scss';

function ListStarships(props) {

  const stops = (consumable) => {
     const consumables = props.allShips.map(item => item && item.consumables ? 
      item.consumables.split(' ') : undefined);

      switch (consumables[1]) {
				case 'day':
					case 'days':
            consumable = parseInt(consumables[0]) * 24;
					break;
					case 'week':
				case 'weeks':
					consumable = parseInt(consumables[0]) * 168;
					break;
					case 'month':
					case 'months':
            consumable = parseInt(consumables[0]) * 730;
					break;
					case 'year':
				case 'years':
            consumable = parseInt(consumables[0]) * 8760;
						break;
				default:
          consumable = 0;
					break;
        } 
  }

  return (
    <>
       <section>
        <List component="nav" aria-label="main mailbox folders">
          {props.allShips.map(item => item && item.name ? 
            <ListItem className="ships">
              <ListItemText className="shipsName">{item.name} - Will make {1000000 / (item.MGLT * stops(item.consumables))} stops</ListItemText>
            </ListItem> : <p></p>)
          }
        </List>
      </section>
    </>
  );
};

export default ListStarships;
