import React from "react";
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';

import './styles.scss';

function ListStarships(props) {
  const [timeCool, setTimeCool] = useState(0)
  const stops = () => {
    const consumables = props.allShips.map(item => item && item.consumables ? 
      item.consumables.split(' ') : undefined);

      switch (consumables[1]) {
				case 'day':
					case 'days':
					time = parseInt(consumables[0]) * 24;
					break;
					case 'week':
				case 'weeks':
						time = parseInt(consumables[0]) * 168;
					break;
					case 'month':
					case 'months':
					time = parseInt(consumables[0]) * 730;
					break;
					case 'year':
				case 'years':
						time = parseInt(consumables[0]) * 8760;
						break;
				default:
						time = 0;
					break;
        }


  }
  return (
    <>
       <section>
        <List component="nav" aria-label="main mailbox folders">
          {props.allShips.map(item => item && item.name ? 
            <ListItem className="ships">
              <ListItemText className="shipsName">{item.name} - Will make {stops()} stops</ListItemText>
            </ListItem> : <p></p>)
          }
        </List>
      </section>
    </>
  );
};

export default ListStarships;
